import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import StatsPanel from "./StatsPanel";
import CardComponent from "./CardComponent";
import { loadAllCards } from "./data/cards";
import "../../styles/game.css";

const applyPower = (card, opponent) => {
  let modifiedCard = { ...card, stats: { ...card.stats } };
  let modifiedOpponent = { ...opponent, stats: { ...opponent.stats } };

  const power = (card.power || "").toLowerCase();

  if (power.includes("assassinat") && opponent.stats.conscience < 3) {
    modifiedCard.stats.influence += 99;
  }

  if (
    power.includes("bonus contre reptilien") &&
    opponent.stats.origine.toLowerCase().includes("reptilien")
  ) {
    modifiedCard.stats.influence += 2;
  }

  if (power.includes("protection divine")) {
    modifiedCard.stats.conscience += 1;
  }

  if (power.includes("vol d‚Äô√©nergie")) {
    modifiedCard.stats.influence += 1;
    modifiedOpponent.stats.influence = Math.max(
      0,
      modifiedOpponent.stats.influence - 1
    );
  }

  return { modifiedCard, modifiedOpponent };
};

const compareCards = (card1, card2) => {
  const { modifiedCard: c1 } = applyPower(card1, card2);
  const { modifiedCard: c2 } = applyPower(card2, card1);

  const total1 = c1.stats.influence + c1.stats.conscience;
  const total2 = c2.stats.influence + c2.stats.conscience;

  if (total1 > total2) return "player";
  if (total2 > total1) return "bot";
  return "draw";
};

const Arena = () => {
  const [playerHand, setPlayerHand] = useState([]);
  const [botHand, setBotHand] = useState([]);
  const [round, setRound] = useState(1);
  const [message, setMessage] = useState("");
  const [selectedCard, setSelectedCard] = useState(null);
  const [botSelectedCard, setBotSelectedCard] = useState(null);
  const [score, setScore] = useState({ player: 0, bot: 0 });
  const [gameOver, setGameOver] = useState(false);

  const setupGame = async () => {
    const allCards = await loadAllCards();
    const shuffled = allCards.sort(() => 0.5 - Math.random());
    setPlayerHand(shuffled.slice(0, 5));
    setBotHand(shuffled.slice(5, 10));
    setRound(1);
    setScore({ player: 0, bot: 0 });
    setMessage("");
    setSelectedCard(null);
    setBotSelectedCard(null);
    setGameOver(false);
  };

  useEffect(() => {
    setupGame();
  }, []);

  const handleCardSelect = (card, index) => {
    if (gameOver) return;

    const botIndex = Math.floor(Math.random() * botHand.length);
    const botCard = botHand[botIndex];

    setSelectedCard(card);
    setBotSelectedCard(botCard);

    const winner = compareCards(card, botCard);

    if (winner === "player") {
      setMessage("‚úÖ Tu gagnes ce tour !");
      setScore((prev) => ({ ...prev, player: prev.player + 1 }));
    } else if (winner === "bot") {
      setMessage("ü§ñ Le robot gagne ce tour !");
      setScore((prev) => ({ ...prev, bot: prev.bot + 1 }));
    } else {
      setMessage("‚öñÔ∏è √âgalit√© !");
    }

    setPlayerHand((prev) => prev.filter((_, i) => i !== index));
    setBotHand((prev) => prev.filter((_, i) => i !== botIndex));

    if (round >= 5) {
      setGameOver(true);
      setMessage((prev) => prev + " üéâ Fin de la partie !");
    }

    setRound((r) => r + 1);
  };

  return (
    <div className="game-container">
      <Navbar />

      <div className="game-page">
        <h1>‚öîÔ∏è Ar√®ne de Combat</h1>
        <p>Tour {round <= 5 ? round : 5} / 5</p>
        <p>{message}</p>

        <div className="scoreboard">
          <p>üßô‚Äç‚ôÇÔ∏è Toi : {score.player}</p>
          <p>ü§ñ Bot : {score.bot}</p>
        </div>

        {selectedCard && botSelectedCard && (
          <div className="arena-battle">
            <CardComponent card={selectedCard} />
            <span style={{ fontSize: "2em", margin: "0 20px" }}>VS</span>
            <CardComponent card={botSelectedCard} />
          </div>
        )}

        {!gameOver && (
          <>
            <h2>üÉè Ta main</h2>
            <div className="card-row">
              {playerHand.map((card, i) => (
                <div key={i} onClick={() => handleCardSelect(card, i)}>
                  <CardComponent card={card} />
                </div>
              ))}
            </div>
          </>
        )}

        {gameOver && (
          <button
            className="deal-button"
            style={{ marginTop: "20px" }}
            onClick={setupGame}
          >
            üîÑ Rejouer la partie
          </button>
        )}
      </div>
    </div>
  );
};

export default Arena;
