import React, { useState } from "react";
import Navbar from "../components/Navbar";
import "../styles/game.css";
import CardHand from "./game/CardHand";
import StoryText from "./game/StoryText";
import StatsPanel from "./game/StatsPanel";
import { initialHand } from "./game/data/cards";

const Game = () => {
  const [hand] = useState(initialHand);
  const [stats] = useState({
    influence: 5,
    conscience: 3,
    origine: "Hybride",
  });

  return (
    <div className="game-container">
      <Navbar />

      <div className="mystic-background">
        <div className="mystic-smoke"></div>
      </div>

      <div className="game-page">
        <h1>Partie en cours</h1>
        <p>Ici, le jeu de cartes s'affiche et Ã©volue avec l'histoire.</p>

        {/* Composants du moteur de jeu */}
        <StatsPanel stats={stats} />
        <StoryText />
        <CardHand cards={hand} />

        <div className="logo-container">
          <img src="/logo512.png" alt="Codex Arcana" />
        </div>
      </div>
    </div>
  );
};

export default Game;
