import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import StatsPanel from "./StatsPanel";
import CardComponent from "./CardComponent";
import { loadAllCards } from "./data/cards";
import "../../styles/game.css";

// Applique le pouvoir d'une carte sur l'autre (simplifi√© ici)
const applyPower = (source, target) => {
  const modifiedTarget = { ...target };
  const modifiedSource = { ...source };

  const power = source.power?.toLowerCase();

  if (power?.includes("affaiblit")) {
    modifiedTarget.stats.influence = Math.max(0, target.stats.influence - 2);
  }
  if (power?.includes("boost")) {
    modifiedSource.stats.influence += 2;
  }

  return { modifiedCard: modifiedSource, modifiedOpponent: modifiedTarget };
};

// Comparaison avec pouvoirs
const compareCards = (card1, card2) => {
  const { modifiedCard: c1, modifiedOpponent: c2From1 } = applyPower(
    card1,
    card2
  );
  const { modifiedCard: c2, modifiedOpponent: c1From2 } = applyPower(
    card2,
    card1
  );

  const total1 = c1.stats.influence + c1.stats.conscience;
  const total2 = c2.stats.influence + c2.stats.conscience;

  if (total1 > total2) return "player";
  if (total2 > total1) return "bot";
  return "draw";
};

const Arena = () => {
  const [playerHand, setPlayerHand] = useState([]);
  const [botHand, setBotHand] = useState([]);
  const [round, setRound] = useState(1);
  const [message, setMessage] = useState("");
  const [selectedPair, setSelectedPair] = useState(null);
  const [botPair, setBotPair] = useState(null);
  const [score, setScore] = useState({ player: 0, bot: 0 });
  const [gameOver, setGameOver] = useState(false);
  const [playerRace, setPlayerRace] = useState("Hybride");

  const setupGame = async () => {
    const allCards = await loadAllCards();
    const shuffled = allCards.sort(() => 0.5 - Math.random());

    // 1 personnage choisi + 9 al√©atoires
    const playerCharacter = allCards.find(
      (c) => c.stats.origine.toLowerCase() === playerRace.toLowerCase()
    );
    const playerRest = shuffled
      .filter((c) => c !== playerCharacter)
      .slice(0, 9);
    const botCards = shuffled.slice(9, 19);

    setPlayerHand([playerCharacter, ...playerRest]);
    setBotHand(botCards);
    setRound(1);
    setScore({ player: 0, bot: 0 });
    setMessage("");
    setSelectedPair(null);
    setBotPair(null);
    setGameOver(false);
  };

  useEffect(() => {
    setupGame();
  }, [playerRace]);

  const playRound = (index1, index2) => {
    if (gameOver) return;

    const card1 = playerHand[index1];
    const card2 = playerHand[index2];

    const botIndexes = botHand.length >= 2 ? [0, 1] : [0];
    const botCards = botIndexes.map((i) => botHand[i]);

    setSelectedPair([card1, card2]);
    setBotPair(botCards);

    const totalPlayer =
      compareCards(card1, botCards[0]) + compareCards(card2, botCards[1] || {});
    let winner;

    if (totalPlayer === "player") {
      setScore((prev) => ({ ...prev, player: prev.player + 1 }));
      winner = "‚úÖ Tu gagnes ce round !";
    } else if (totalPlayer === "bot") {
      setScore((prev) => ({ ...prev, bot: prev.bot + 1 }));
      winner = "ü§ñ Le bot gagne ce round !";
    } else {
      winner = "‚öñÔ∏è √âgalit√© !";
    }

    setMessage(winner);

    const updatedPlayer = playerHand.filter(
      (_, i) => i !== index1 && i !== index2
    );
    const updatedBot = botHand.filter((_, i) => !botIndexes.includes(i));
    setPlayerHand(updatedPlayer);
    setBotHand(updatedBot);

    if (round >= 5) {
      setGameOver(true);
      const finalMessage =
        score.player > score.bot
          ? "üèÜ Tu remportes la partie !"
          : score.bot > score.player
          ? "üíÄ Le bot remporte la partie !"
          : "ü§ù Match nul !";
      setMessage(finalMessage);
    } else {
      setRound((r) => r + 1);
    }
  };

  return (
    <div className="game-container">
      <Navbar />
      <div className="game-page">
        <h1>‚öîÔ∏è Ar√®ne de Combat</h1>
        <p>Tour {round} / 5</p>
        <p>{message}</p>

        <div className="scoreboard">
          <p>üßô‚Äç‚ôÇÔ∏è Toi : {score.player}</p>
          <p>ü§ñ Bot : {score.bot}</p>
        </div>

        {selectedPair && botPair && (
          <div className="arena-battle">
            {selectedPair.map((c, i) => (
              <CardComponent key={i} card={c} />
            ))}
            <span style={{ fontSize: "2em", margin: "0 20px" }}>VS</span>
            {botPair.map((c, i) => (
              <CardComponent key={i} card={c} />
            ))}
          </div>
        )}

        {!gameOver && (
          <>
            <h2>üÉè Ta main</h2>
            <p>(Clique sur 2 cartes pour former ton √©quipe)</p>
            <div className="card-row">
              {playerHand.map((card, i) => (
                <div key={i} onClick={() => handleSelectCard(i)}>
                  <CardComponent card={card} />
                </div>
              ))}
            </div>
          </>
        )}

        {gameOver && (
          <button
            onClick={setupGame}
            className="deal-button"
            style={{ marginTop: 20 }}
          >
            üîÑ Rejouer
          </button>
        )}
      </div>
    </div>
  );
};

// S√©lection de 2 cartes cons√©cutives
let selectedIndexes = [];
const handleSelectCard = (index) => {
  selectedIndexes.push(index);
  if (selectedIndexes.length === 2) {
    const [i1, i2] = selectedIndexes;
    selectedIndexes = [];
    document.querySelector(".game-page").dispatchEvent(
      new CustomEvent("play-round", {
        detail: { i1, i2 },
      })
    );
  }
};

export default Arena;
