import React from "react";
import CardComponent from "./CardComponent";

const CardHand = ({ cards }) => {
  // Regroupement par catégorie (origine : Personnage, Artefact, Pouvoir)
  const categories = {
    Personnages: cards.filter(
      (c) =>
        c.stats.origine &&
        c.stats.origine !== "Artefact" &&
        c.stats.origine !== "Pouvoir" &&
        c.stats.origine !== "Événement"
    ),
    Artefacts: cards.filter((c) => c.stats.origine === "Artefact"),
    Pouvoirs: cards.filter(
      (c) => c.stats.origine === "Pouvoir" || c.stats.origine === "Événement"
    ),
  };

  return (
    <div className="card-hand-wrapper">
      {Object.entries(categories).map(([cat, group]) => (
        <div key={cat}>
          <h3 className="category-title">{cat}</h3>
          <div className="card-row">
            {group.map((card, index) => (
              <CardComponent key={index} card={card} />
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default CardHand;
