// src/pages/Home.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../components/Navbar";

const Home = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    username: "",
    description: "",
    profilePic: null,
  });
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(res.data);
        setFormData({
          username: res.data.username || "",
          description: res.data.description || "",
          profilePic: null,
        });
      } catch (err) {
        console.error("Erreur profil:", err);
      }
    };
    fetchProfile();
  }, [token]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({
      ...formData,
      [name]: files ? files[0] : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    try {
      const form = new FormData();
      form.append("username", formData.username);
      form.append("description", formData.description);
      if (formData.profilePic) form.append("profilePic", formData.profilePic);

      const res = await axios.put("http://localhost:5000/api/profile", form, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });
      setUser(res.data);
      setMessage("Profil mis à jour avec succès !");
    } catch (err) {
      console.error("Erreur mise à jour:", err);
      setMessage("Erreur lors de la mise à jour du profil.");
    }
  };

  if (!user) return <p>Chargement...</p>;

  return (
    <>
      <Navbar />
      <div style={{ maxWidth: 600, margin: "0 auto" }}>
        <h1>Bienvenue dans Codex Arcana, {user.name || user.username}</h1>
        <p>Ici commence ta légende !</p>

        {user.profilePic && (
          <img
            src={`http://localhost:5000${user.profilePic}`}
            alt="Profil"
            style={{ width: 100, borderRadius: "50%" }}
          />
        )}

        <p>
          <strong>Points:</strong> {user.points}
        </p>
        <p>
          <strong>Historique:</strong> {user.history?.join(", ")}
        </p>

        <form onSubmit={handleSubmit}>
          <h3>Modifier ton profil</h3>
          <input
            type="text"
            name="username"
            placeholder="Nom d'utilisateur"
            value={formData.username}
            onChange={handleChange}
          />
          <textarea
            name="description"
            placeholder="Description"
            value={formData.description}
            onChange={handleChange}
          ></textarea>
          <input type="file" name="profilePic" onChange={handleChange} />
          <button type="submit">Enregistrer</button>
        </form>
        {message && <p>{message}</p>}
      </div>
    </>
  );
};

export default Home;
